import React, { useState } from 'react';
import axios from 'axios'
import '../views/feedback.css'
import styled from 'styled-components';

const FeedbackContainer = styled.div`
body {
  font-family: 'Arial', sans-serif;
  background-color: #f8f9fa;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
}

form {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
  max-width: 300px;
  width: 100%;
  margin-top: 300px; /* Adjust the margin from the top */
}

h2 {
  text-align: center;
  color: #333;
}

label {
  display: block;
  margin-bottom: 10px;
  font-weight: bold;
  color: #333;
}

input,
select,
textarea {
  width: 100%;
  padding: 12px;
  margin-bottom: 15px;
  box-sizing: border-box;
  border: 1px solid #ddd;
  border-radius: 6px;
  background-color: #f9f9f9;
}

table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 15px;
}

th, td {
  padding: 12px;
  text-align: center;
}

table input[type="radio"] {
  border: none;
}

button {
  background-color: rgb(31, 6, 168);
  color: #fff;
  padding: 14px 20px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s ease;
}

button:hover {
  background-color: #1e2df6;
}

@media only screen and (max-width: 600px) {
  form {
    width: 90%;
  }
}

`;

const Feedback = () => {

    const [values, setValues] = useState({
        fname: '',
        lname:'',
        email: '',
        ttype: '',
        dateRange: '',
        testDate: '',
        venue: '',
        staff: '',
        equipment: '',
        overall: '',
        feedback: '',
      });
      const handleChange = (event) => {
        setValues({ ...values, [event.target.name]: event.target.value });
      };
      
      const handleSubmit = (event) => {
        event.preventDefault();
      
        axios.post('http://localhost:8081/pcr3', values)
          .then(res => {
            console.log("Data sent Successfully!");
            //toast.success("Thank you for your valuable response ðŸ™‡");
          })
          .catch(err => {
            console.log(err);
           // toast.error("Oops something went wrong!");
          });
      };
      
  return (
    <div>
    <form onSubmit={handleSubmit}>
    <h2><center>Event-Servey</center></h2>
    <div>
      <label>Name</label>
      <input type="text" name="fname" onChange={handleChange} placeholder='First Name' required/>
    </div>
    <div>
      <input type="text" name="lname"  onChange={handleChange} placeholder='Last Name' required/>
    </div>
    <div>
      <label>Email</label>
      <input type="email" name="email"  onChange={handleChange} placeholder='email' required/>
    </div>
    <div>
    <div>
          <label>Test category</label>
          <select name="ttype" onChange={handleChange} required>
            <option value="">Select...</option>
            <option value="PCR-Test">PCR Test</option>
            <option value="Antigent-Test">Antigent-Test</option>
          </select>
        </div>
        <div>
          <label>Select the Date Range</label>
          <input
            type="date"
            name="dateRange"
            value={values.dateRange}
            onInput={handleChange}
            required
          />
        </div>
        <div>
          <label>Test Date</label>
          <input
            type="date"
            name="testDate"
            value={values.testDate}
            onInput={handleChange}
            required
          />
        </div>
