import React, { useState, useEffect, useCallback, useRef } from "react"
import { withRouter, useLocation } from "react-router-dom"
import { Col, message, Row, Steps, Divider, Modal, Button } from "antd"
import styled from "styled-components"
import io from "socket.io-client"
import * as Commons from "common/common"


const { Step } = Steps

const CustomSteps = styled(Steps)`
  .ant-steps-item-wait
    > .ant-steps-item-container
    > .ant-steps-item-content
    > .ant-steps-item-title {
    color: rgba(0, 0, 0, 0.2);
  }
  .ant-steps-item-wait
    > .ant-steps-item-container
    > .ant-steps-item-content
    > .ant-steps-item-description {
    color: rgba(0, 0, 0, 0.2);
  }
  .ant-steps-item-process
    > .ant-steps-item-container
    > .ant-steps-item-content
    > .ant-steps-item-title {
    color: #21acd7;
    font-weight: bold;
  }
  .ant-steps-item-process
    > .ant-steps-item-container
    > .ant-steps-item-content
    > .ant-steps-item-description {
    color: #21acd7;
    font-weight: bold;
  }
  .ant-steps-item-finish
    > .ant-steps-item-container
    > .ant-steps-item-content
    > .ant-steps-item-title {
    color: rgba(0, 0, 0, 0.6);
    font-weight: bold;
  }
  .ant-steps-item-finish
    > .ant-steps-item-container
    > .ant-steps-item-content
    > .ant-steps-item-description {
    color: rgba(0, 0, 0, 0.6);
    font-weight: bold;
  }
`
// Button CSS for Feedback Start
const centerStyle = {
  display: "flex",
  justifyContent: "center",
  alignItems: "flex-start",
  minHeight: "100vh",
  paddingTop: "20px",
};

const initialFeedbackButtonStyle = {
  padding: "10px 20px",
  fontSize: "16px",
  backgroundColor: "#0BA8E6",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  transition: "background-color 0.3s",
  animation: "blink 1s infinite", 
};

const hoverFeedbackButtonStyle = {
  backgroundColor: "#87CEFA",
};


// Button CSS for Feedback Ends

const CustomStep = styled(Step)`
  &::after {
    display: none !important;
  }
  .ant-steps-item-icon {
    display: none !important;
  }
`

const OccasionType = (props) => {
  const [isHovered, setIsHovered] = useState(false);
  const { history, showLoadingPageSpin, hideLoadingPageSpin, auth } = props
  const location = useLocation()
  const registeredOccasionsRef = useRef()

  const [registeredOccasion, setRegisteredOccasion] = useState([])

  const fetchRegisteredOccasion = useCallback(() => {
    showLoadingPageSpin()

    Commons.axiosInstance
      .get(Commons.apiClientRegistrations)
      .then((response) => {
        setRegisteredOccasion(response?.data || [])

        if (response?.data?.length > 0) {
          history.push(`${Commons.clientRegisteredOccasionsRoute}`)
        } else {
          if (location?.state?.login) {
            Modal.info({
              title: "",
              icon: null,
              className: "whitespace-pre-wrap text-center",
              content: (
                <p>
                  „ÄêÊ≥®ÊÑè‰∫ãÈ†Ö„Äë
                  <br />
                  <span className="text-red-600">
                    9Êúà1Êó•‰ª•Èôç„ÄÄÊÑõÁü•ÁúåÂú®‰Ωè„ÅÆÊñπ‰ª•Â§ñ„ÅØÊúâÊñôÊ§úÊüª„Å®„Å™„Çä„Åæ„Åô
                  </span>
                  <br />
                  2ÂõûÁõÆ‰ª•Èôç„ÅÆÊ§úÊüª‰∫àÁ¥Ñ„Çí„Åï„Çå„ÇãÊÑõÁü•ÁúåÂú®‰Ωè„ÅÆÊñπ„ÅØ„ÄÅÊ§úÊüªÁõÆÁöÑ„Çí„Äå2.ÊÑüÊüì‰∏çÂÆâ„Åå„ÅÇ„Çã„Åü„ÇÅ„Äç„Å´Â§âÊõ¥„Çí
                  <br />
                  „ÅäÈ°ò„ÅÑ„Åó„Åæ„Åô„ÄÇ
